INSERT IGNORE INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES
(32204, @GROUP_ID+0, @ID+0, '一名传送门看护者冲出了传送门！', 41, 0, 100, 0, 0, 0, 32996, '辛克莱尔中尉 to 传送门'),
(32204, @GROUP_ID+1, @ID+0, '传送门里涌出一支燃烧军团精英小队！', 41, 0, 100, 0, 0, 0, 104686, '辛克莱尔中尉 to 督军马尔加斯'),
(32204, @GROUP_ID+2, @ID+0, '传送门守卫者保护着新的传送门！', 41, 0, 100, 0, 0, 0, 32995, '辛克莱尔中尉 to 传送门'),
(101976, @GROUP_ID+0, @ID+0, '总算摆脱那该死的陷阱了！真是——', 14, 0, 100, 0, 0, 57475, 104411, '米尔菲丝·法力风暴 to Player'),
(101976, @GROUP_ID+1, @ID+0, '等等，我认得那个声音……', 12, 0, 100, 0, 0, 57473, 104413, '米尔菲丝·法力风暴 to Player'),
(101976, @GROUP_ID+2, @ID+0, '米尔豪斯！！下一个就是你！', 14, 0, 100, 0, 0, 57471, 104415, '米尔菲丝·法力风暴 to Player'),
(101976, @GROUP_ID+3, @ID+0, '你准备好爆炸了吗？', 14, 0, 100, 0, 0, 57470, 104856, '米尔菲丝·法力风暴 to Player'),
(101976, @GROUP_ID+4, @ID+0, '别叫我“米莉”，米尔豪斯·杰瑞姆斯·法力风暴！看我收拾你！', 14, 0, 100, 0, 0, 57484, 104425, '米尔菲丝·法力风暴'),
(102040, @GROUP_ID+0, @ID+0, '嘿，你！我正在海滩上享受悠闲假期呢，突然感觉到一阵寒风刮过。我说，你到底在打什么鬼主意？', 12, 0, 100, 0, 0, 57474, 104412, '米尔豪斯·法力风暴的影像 to 米尔菲丝·法力风暴'),
(102040, @GROUP_ID+1, @ID+0, '哦，该死的，你干了什么？快，把她关回牢房去！', 14, 0, 100, 0, 0, 57472, 104414, '米尔豪斯·法力风暴的影像 to 米尔菲丝·法力风暴'),
(102040, @GROUP_ID+2, @ID+0, '现在嘲笑她的头发吧！这没用，但这会让她非常抓狂！', 12, 0, 100, 0, 0, 57480, 104418, '米尔豪斯·法力风暴的影像 to 米尔菲丝·法力风暴'),
(102040, @GROUP_ID+3, @ID+0, '听我说，米莉，我可以解释！', 12, 0, 100, 0, 0, 57485, 104424, '米尔豪斯·法力风暴的影像 to 米尔菲丝·法力风暴'),
(102040, @GROUP_ID+4, @ID+0, '那才不是我的中间名呢！', 14, 0, 100, 0, 0, 57483, 104426, '米尔豪斯·法力风暴的影像 to 米尔菲丝·法力风暴'),
(102040, @GROUP_ID+5, @ID+0, '真是多谢了。你根本不明白自己放出来的是什么东西！', 12, 0, 100, 0, 0, 57482, 104427, '米尔豪斯·法力风暴的影像 to 米尔菲丝·法力风暴'),
(102040, @GROUP_ID+6, @ID+0, '说句题外话，我……呃……我还有点事……', 12, 0, 100, 0, 0, 57481, 104428, '米尔豪斯·法力风暴的影像 to 米尔菲丝·法力风暴'),
(102278, @GROUP_ID+0, @ID+0, '监狱守卫们，准备撤退了！这些冒险者将接管这里的防御事务！迅速撤离！', 14, 0, 100, 5, 0, 0, 31474, '辛克莱尔中尉 to Player'),
(102278, @GROUP_ID+1, @ID+0, '我要锁上大门了。祝你们好运，感谢你们的帮助。', 12, 0, 100, 396, 0, 0, 31475, '辛克莱尔中尉 to Player'),
(102282, @GROUP_ID+0, @ID+0, '我见识过的恐怖足以让你最黑暗的噩梦都黯然失色，可就连我都没见过这么可怕的玩意儿……一个愤怒的侏儒发明家。', 14, 0, 100, 0, 0, 57930, 107017, '督军马尔加斯 to Player'),
(102282, @GROUP_ID+1, @ID+0, '啊……就连我都听说过颤栗之喉。她是一头堕落的蓝龙，原本名叫爱尔达苟萨。她一定能让达拉然的天空都瑟瑟发抖。', 14, 0, 100, 0, 0, 57933, 107021, '督军马尔加斯 to Player'),
(102282, @GROUP_ID+2, @ID+0, '够了！这些囚犯都是废物。还是老话说的好：“你想要好好毁掉一座浮空城的话，就得亲自上阵。”', 14, 0, 100, 0, 0, 57939, 107029, '督军马尔加斯 to Player'),
(102282, @GROUP_ID+3, @ID+0, '你们……迟早会……完蛋的……', 14, 0, 100, 0, 0, 57946, 107036, '督军马尔加斯 to Player'),
(102302, @GROUP_ID+0, @ID+0, '进入达拉然的传送门开启了！', 14, 0, 100, 0, 0, 0, 31657, '传送门看护者 to 传送门'),
(102302, @GROUP_ID+1, @ID+0, '就算我死了，这次入侵行动也不会停止！', 14, 0, 100, 0, 0, 0, 31655, '传送门看护者 to Player'),
(102302, @GROUP_ID+2, @ID+0, '传送门已经稳定！发动进攻！', 14, 0, 100, 0, 0, 0, 31524, '传送门看护者 to 传送门'),
(102336, @GROUP_ID+0, @ID+0, '你们无法摧毁所有的传送门！', 14, 0, 100, 0, 0, 57933, 31654, '传送门看护者 to Player'),
(102337, @GROUP_ID+0, @ID+0, '达拉然注定要毁灭！', 14, 0, 100, 0, 0, 0, 31658, '传送门守卫者 to 传送门'),
(102387, @GROUP_ID+0, @ID+0, '你逃不掉的。你是我的！', 12, 0, 100, 0, 0, 57600, 106151, '赛尔奥隆'),
(102387, @GROUP_ID+1, @ID+0, '别回头。', 12, 0, 100, 0, 0, 57593, 106143, '赛尔奥隆'),
(102387, @GROUP_ID+2, @ID+0, '还来了一只蜘蛛……', 12, 0, 100, 0, 0, 57594, 106144, '赛尔奥隆'),
(102387, @GROUP_ID+3, @ID+0, '跑吧。我喜欢看你们逃跑。', 12, 0, 100, 0, 0, 57595, 106145, '赛尔奥隆'),
(102387, @GROUP_ID+4, @ID+0, '我都说了要慢慢杀掉你了……可他们就是不听。', 12, 0, 100, 0, 0, 57595, 106146, '赛尔奥隆'),
(102387, @GROUP_ID+5, @ID+0, '什么！？你为什么要这么做？', 12, 0, 100, 0, 0, 57592, 106142, '赛尔奥隆 to Player');
