(95676, @GROUP_ID+3, @ID+0, '我好久没遇到过像样的对手了。来战斗吧！', 14, 0, 100, 0, 0, 54604, 102913, '奥丁'),
(95676, @GROUP_ID+4, @ID+0, '够了！我……认输！你们真是了不起。我说过，你们会得到应得的奖赏。', 12, 0, 100, 0, 0, 54598, 102907, '奥丁'),
(95833, @GROUP_ID+0, @ID+0, '我不会让你失望的，艾尔！', 14, 0, 100, 0, 0, 54687, 102993, '赫娅'),
(95833, @GROUP_ID+1, @ID+0, '|TInterface\\Icons\\Spell_Holy_HolyNova:20|t赫娅开始施放|cFFFF0404|Hspell:192206|h[圣洁庇护]|h|r！', 41, 0, 100, 0, 0, 0, 122900, '赫娅'),
(95833, @GROUP_ID+2, @ID+0, '你休想继续玷污这座殿堂！', 14, 0, 100, 0, 0, 54681, 102986, '赫娅'),
(95833, @GROUP_ID+3, @ID+0, '对不起，艾尔……我让你失望了……', 14, 0, 100, 0, 0, 54681, 102987, '赫娅 to Player'),
(95843, @GROUP_ID+0, @ID+0, '我会好好享受碾压你们的快感的！', 14, 0, 100, 0, 0, 54577, 102583, '赫尔多国王 to 赫尔多国王'),
(95843, @GROUP_ID+1, @ID+0, '我的力量……消散了。', 14, 0, 100, 0, 0, 54575, 102585, '赫尔多国王 to Player'),
(97081, @GROUP_ID+0, @ID+0, '他们的失败就是我们的胜利！', 14, 0, 100, 0, 0, 54570, 102590, '布约恩国王'),
(97081, @GROUP_ID+1, @ID+0, '你们……击败了我。', 14, 0, 100, 0, 0, 54569, 102592, '布约恩国王 to Player'),
(97083, @GROUP_ID+0, @ID+0, '你们痛苦的哀嚎将永世回荡！', 14, 0, 100, 0, 0, 54574, 102586, '拉努夫国王 to 拉努夫国王'),
(97083, @GROUP_ID+1, @ID+0, '我拥抱深渊！', 14, 0, 100, 0, 0, 54572, 102588, '拉努夫国王 to Player'),
(97084, @GROUP_ID+0, @ID+0, '看来我……低估了你们。', 14, 0, 100, 0, 0, 54578, 102582, '托尔国王 to Player'),
(97202, @GROUP_ID+0, @ID+0, '你休想妨碍赫娅的晋升！', 14, 0, 100, 0, 0, 54670, 102975, '启迪者奥米尔'),
(97202, @GROUP_ID+1, @ID+0, '愿圣光永远照耀你，赫娅！', 14, 0, 100, 0, 0, 54671, 102976, '启迪者奥米尔 to Player'),
